import React from 'react';
import {
	Text,
	TouchableOpacity,
	View,
	StyleSheet,
	ScrollView,
	ImageBackground,
	FlatList,
	Image,
	LogBox,
} from 'react-native';
import { dummyData, COLORS, SIZES, FONTS, icons, images } from '../constants';
import { PriceAlert, TransactionHistory } from '../components';

const Home = ({ navigation }) => {
	React.useEffect(() => {
		LogBox.ignoreLogs(['VirtualizedLists should never be nested']);
	}, []);
	// renderHeader
	function renderHeader() {
		const renderItems = ({ item, index }) => {
			return (
				<TouchableOpacity
					style={{
						width: 180,
						paddingVertical: SIZES.padding,
						paddingHorizontal: SIZES.padding,
						marginLeft: index == 0 ? SIZES.padding : 0,
						marginRight: SIZES.radius,
						borderRadius: 10,
						backgroundColor: COLORS.white,
					}}
					onPress={() => navigation.navigate('CryptoDetail', { currency: item })}
				>
					{/* Currency */}
					<View style={{ flexDirection: 'row' }}>
						<View>
							<Image
								source={item.image}
								resizeMode='cover'
								style={{
									marginTop: 5,
									width: 25,
									height: 25,
								}}
							/>
						</View>
						<View style={{ marginLeft: SIZES.base }}>
							<Text style={{ ...FONTS.h2 }}>{item.currency}</Text>
							<Text style={{ color: COLORS.gray, ...FONTS.body3 }}>{item.code}</Text>
						</View>
					</View>

					{/* Value */}
					<View style={{ marginTop: SIZES.radius }}>
						<Text style={{ ...FONTS.h2 }}>${item.amount}</Text>
						<Text style={{ color: item.type == 'I' ? COLORS.green : COLORS.red, ...FONTS.h3 }}>
							{item.changes}
						</Text>
					</View>
				</TouchableOpacity>
			);
		};
		return (
			<View
				style={{
					width: '100%',
					height: 290,
					...styles.shadow,
				}}
			>
				<ImageBackground
					source={images.banner}
					resizeMode='cover'
					style={{
						flex: 1,
						alignItems: 'center',
					}}
				>
					{/* Header Bar */}
					<View
						style={{
							marginTop: SIZES.padding * 2,
							width: '100%',
							alignItems: 'flex-end',
							paddingHorizontal: SIZES.padding,
						}}
					>
						<TouchableOpacity
							style={{
								width: 35,
								height: 35,
								justifyContent: 'center',
								alignItems: 'center',
							}}
							onPress={() => console.warn('notification pressed')}
						>
							<Image
								source={icons.notification_white}
								resizeMode='contain'
								style={{
									flex: 1,
								}}
							/>
						</TouchableOpacity>
					</View>
					{/* Balance */}
					<View
						style={{
							alignItems: 'center',
							justifyContent: 'center',
						}}
					>
						<Text style={{ color: COLORS.white, ...FONTS.h3 }}>Your Portfolio Balance</Text>
						<Text style={{ color: COLORS.white, marginTop: SIZES.base, ...FONTS.h1 }}>
							${dummyData.portfolio.balance}
						</Text>
						<Text style={{ color: COLORS.white, ...FONTS.body5 }}>
							${dummyData.portfolio.changes} Last 24 hours
						</Text>
					</View>

					{/* Trending */}
					<View
						style={{
							position: 'absolute',
							bottom: '-30%',
						}}
					>
						<Text style={{ color: COLORS.white, marginLeft: SIZES.padding, ...FONTS.h2 }}>Trending</Text>
						<FlatList
							contentContainerStyle={{ marginTop: SIZES.base }}
							data={dummyData.trendingCurrencies}
							renderItem={renderItems}
							keyExtractor={item => `${item.id}`}
							horizontal
							showsHorizontalScrollIndicator={false}
						/>
					</View>
				</ImageBackground>
			</View>
		);
	}

	// renderPriceAlert
	function renderPriceAlert() {
		return <PriceAlert />;
	}

	// renderNotice
	function renderNotice() {
		return (
			<View
				style={{
					marginTop: SIZES.padding,
					marginHorizontal: SIZES.padding,
					padding: 20,
					borderRadius: SIZES.radius,
					backgroundColor: COLORS.secondary,
					...styles.shadow,
				}}
			>
				<Text style={{ color: COLORS.white, ...FONTS.h3 }}>Investing Safety</Text>
				<Text style={{ color: COLORS.white, marginTop: SIZES.base, ...FONTS.body4, lineHeight: 18 }}>
					It's very difficult to time an investment, especially when the market is volatile. Learn how to use
					dollar cost averaging to your advantage.
				</Text>
				<TouchableOpacity
					style={{
						marginTop: SIZES.base,
					}}
					onPress={() => console.warn('Learn more')}
				>
					<Text style={{ textDecorationLine: 'underline', color: COLORS.green, ...FONTS.h3 }}>Learn More</Text>
				</TouchableOpacity>
			</View>
		);
	}

	// renderTransactionHistory
	function renderTransactionHistory() {
		return <TransactionHistory customContainerStyle={{ ...styles.shadow }} history={dummyData.transactionHistory} />;
	}
	return (
		<ScrollView>
			<View style={{ flex: 1, paddingBottom: 130 }}>
				{renderHeader()}
				{renderPriceAlert()}
				{renderNotice()}
				{renderTransactionHistory()}
			</View>
		</ScrollView>
	);
};

const styles = StyleSheet.create({
	container: {
		flex: 1,
		alignItems: 'center',
		justifyContent: 'center',
	},
	shadow: {
		shadowColor: '#000',
		shadowOffset: {
			width: 0,
			height: 4,
		},
		shadowOpacity: 0.3,
		shadowRadius: 4.65,
		elevation: 8,
	},
});

export default Home;
